class BackendManager(BaseModel):
    """A management tool for interacting with backend database through functions."""

    config: BackendConfig = Field(
        default_factory=lambda: Config.default().backend, description="The uniform backend config", exclude=True
    )
    manager: FuncseaManager | SupabaseManager | None = Field(default=None, description="The used backend manager")
    working_dir: Union[Path, str] = Field(default=DEFAULT_WORKSPACE_ROOT, description="The working directory")
    database_url: str = Field(default="", description="App database URL", exclude=True)

    @property
    def backend_enabled(self) -> bool:
        enabled = True if self.config.backend_type else False  # Whether to use the backend
        be_config_enabled = self.config.config.enable if self.config.config else False  # Whether the specific backend activates it's configuration
        logger.info(
            f"Backend enabled: {enabled}, backend_type: {self.config.backend_type}, config enabled: {be_config_enabled}"
        )
        return enabled

    @property
    def backend_type(self) -> BackendType:
        return self.config.backend_type

    async def set_workdir(self, workdir: str) -> None:
        self.working_dir = Path(workdir)
        if self.manager:
            await self.manager.set_workdir(workdir)

    def set_database_url(self, url: str) -> None:
        """Set runtime database URL."""
        self.database_url = (url or "").strip()
        if self.manager and hasattr(self.manager, "database_url"):
            self.manager.database_url = self.database_url

    @model_validator(mode="after")
    def valid_manager(self):
        if self.config.backend_type == BackendType.FUNCSEA:
            append_func_names = ["insert_table_data"]
            manager_cls = BackendManager
            sub_cls = FuncseaManager
            self.manager = FuncseaManager(
                config=self.config.config,
                working_dir=self.working_dir,
                database_url=self.database_url,
            )
        elif self.config.backend_type == BackendType.SUPABASE:
            append_func_names = []
            manager_cls = BackendManager
            sub_cls = SupabaseManager
            self.manager = SupabaseManager(config=self.config.config, working_dir=self.working_dir)
        else:
            return self
            # raise ValueError(f"Unsupported backend_type: {self.config.backend_type}")

        # register functions only when backend is enabled
        if self.backend_enabled:
            func_names = ["create_table", "get_schemas", "create_function", "get_functions"]
            func_names.extend(append_func_names)
            logger.info(f"{manager_cls.__name__} register {func_names}")
            cls_file_path = inspect.getfile(manager_cls)
            if "metagpt" in cls_file_path:
                cls_file_path = "metagpt" + cls_file_path.split("metagpt")[-1]
            with contextlib.suppress(OSError):
                source_code = inspect.getsource(manager_cls)

            schemas = make_schema(sub_cls, include=func_names, path=None)  # use self.manager to make function schema

            TOOL_REGISTRY.register_tool(
                tool_name=manager_cls.__name__,
                tool_path=cls_file_path,
                schemas=schemas,
                schema_path="",
                tool_code=source_code,
                tags=None,
                tool_source_object=manager_cls,
                include_functions=func_names,
            )
        return self

    async def create_table(self, *args, **kwargs):
        if self.manager:
            return await self.manager.create_table(*args, **kwargs)

    async def get_schemas(self, *args, **kwargs):
        if self.manager:
            return await self.manager.get_schemas(*args, **kwargs)

    async def insert_table_data(self, *args, **kwargs):
        if self.manager:
            return await self.manager.insert_table_data(*args, **kwargs)

    async def create_function(self, *args, **kwargs):
        if self.manager:
            return await self.manager.create_function(*args, **kwargs)

    async def get_functions(self, *args, **kwargs):
        if self.manager:
            return await self.manager.get_functions(*args, **kwargs)
